import Footer from "../components/home/Footer";
import Header from "../components/home/Header";
import BreadcrumbNav from "../components/product/BreadcrumbNav";
import ProductImageGallery from "../components/productdetail/ProductImageGallery";
import ShippingFeatures from "../components/productdetail/ShippingFeatures";
import ProductInfo from "../components/productdetail/ProductInfo";
import ProductOptions from "../components/productdetail/ProductOptions";
import ProductActions from "../components/productdetail/ProductActions";
import ProductDetailSection from "../components/productdetail/ProductDetailSection";
import CustomerReviews from "../components/productdetail/CustomerReviews";
import RecentlyViewed from "../components/product/RecentlyViewed";
import RecommendedProducts from "../components/product/RecommendedProducts";
import CustomerSupport from '../components/productdetail/CustomerSupport';
import QuickSupportSection from './components/QuickSupportSection';

function ProductDetailPage() {
  return (
    <div>
      {/* Header */}
      <Header />

      {/* Breadcrumb */}
      <BreadcrumbNav />

      <div className="product-container">
      {/* Cột trái */}
      <div className="product-image">
        <ProductImageGallery />
      </div>

      {/* Cột phải */}
      <div className="product-details">
        <ProductInfo />
        <ProductOptions />
        <ProductActions />
      </div>

    </div>
        <ShippingFeatures /> {/* Di chuyển vào đây */}

<ProductDetailSection />


      <CustomerReviews />
<RecentlyViewed />
      <RecommendedProducts />

      <CustomerSupport />


      <section class="support-cards">
        <div class="support-card">
          <h4>🛒 Hỗ trợ đặt hàng</h4>
          <p>Hotline tư vấn, hướng dẫn mua hàng</p>
          <small>“Gọi ngay: 1800 1234”</small>
        </div>
        <div class="support-card">
          <h4>🧊 Chính sách đổi trả</h4>
          <p>Hỗ trợ đổi trả trong 7 ngày</p>
          <small>“Xem Chi Tiết”</small>
        </div>
        <div class="support-card">
          <h4>🚀 Giao hàng nhanh</h4>
          <p>Nhận hàng trong 24–48h</p>
          <small>“Miễn phí vận chuyển đơn từ 500K”</small>
        </div>
        <div class="support-card">
          <h4>📞 Hỗ trợ khách hàng 24/7</h4>
          <p>Live chat, hotline, email</p>
          <small>“Liên Hệ Ngay”</small>
        </div>
      </section>

      {/* Footer */}
      <Footer />
    </div>
  );
}

export default ProductDetailPage;
